import json

def process_json(
    first_json_path, 
    second_json_path, 
    match_indices, 
    output_json_path
):
    # 加载两个json
    with open(first_json_path, 'r', encoding='utf-8') as f:
        first_data = json.load(f)

    with open(second_json_path, 'r', encoding='utf-8') as f:
        second_data = json.load(f)

    output_data = []

    # 建立 mapping: 第一个json的index（0-based） -> reasoning文本
    index_to_reasoning = {}
    for idx_in_second, idx_in_first_1based in enumerate(match_indices):
        first_idx = idx_in_first_1based - 1  # 1-based转0-based
        if 0 <= first_idx < len(first_data) and 0 <= idx_in_second < len(second_data):
            reasoning = second_data[idx_in_second]["reasoning"]
            index_to_reasoning[first_idx] = reasoning
        else:
            print(f"[警告] 索引超界：第{idx_in_second+1}条 reasoning 对应第{idx_in_first_1based}条样本，超出范围，跳过。")

    # 顺序遍历第一个json，保持顺序不变
    for idx, item in enumerate(first_data):
        if idx in index_to_reasoning:
            # 有对应reasoning，转换成D需要的格式
            messages = item["messages"][0]["content"]

            # 提取8张图路径
            image_paths = [x["image"] for x in messages if x["type"] == "image"]

            # 提取完整text块
            text_block = [x["text"] for x in messages if x["type"] == "text"][0]

            # 提取完整问题
            if "Let's do this step by step." in text_block:
                question = text_block.split("Let's do this step by step.")[0].strip()
            else:
                raise ValueError(f"Sample idx={idx+1} 没有找到 'Let's do this step by step.'，无法分割问题和instruction！")

            # cot_instruction固定
            cot_instruction = "Let's do this step by step."

            # 构造新item
            output_item = {
                "image_paths": image_paths,
                "question": question,
                "user_info": "",  # 空字符串
                "cot_instruction": cot_instruction,
                "reasoning": index_to_reasoning[idx],
                "label": 0
            }
            output_data.append(output_item)
        else:
            # 没有对应reasoning的，直接保留原样
            output_data.append(item)

    # 保存结果
    with open(output_json_path, 'w', encoding='utf-8') as f:
        json.dump(output_data, f, indent=2, ensure_ascii=False)

    print(f"✅ 处理完成！共保存 {len(output_data)} 条样本到 {output_json_path}")

